// src/forms/CreateEvent.js
import React, { useEffect, useState } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

const schema = Yup.object({
  name: Yup.string().required("Name is required"),
  description: Yup.string().required("Description is required"),
  date: Yup.string().required("Date is required"),
  trees_planted: Yup.number()
    .positive("Must be positive")
    .integer("Must be an integer")
    .required("Trees planted is required"),
  club_id: Yup.number().required("Club is required"),
});

function CreateEvent() {
  const [clubs, setClubs] = useState([]);

  useEffect(() => {
    fetch("/api/clubs")
      .then((res) => res.json())
      .then(setClubs)
      .catch((err) => console.error("Error loading clubs:", err));
  }, []);

  return (
    <div>
      <h3>Create a New Event</h3>
      <Formik
        initialValues={{
          name: "",
          description: "",
          date: "",
          trees_planted: "",
          club_id: "",
        }}
        validationSchema={schema}
        onSubmit={(values, { setSubmitting, resetForm }) => {
          const payload = {
            ...values,
            date: new Date(values.date).toISOString(),
          };
          fetch("/api/events", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
          })
            .then((res) => res.json())
            .then(() => {
              alert("Event created!");
              resetForm();
            })
            .catch(() => alert("Error creating event"))
            .finally(() => setSubmitting(false));
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <label>Name</label>
            <Field name="name" />
            <ErrorMessage name="name" component="div" />

            <label>Description</label>
            <Field name="description" />
            <ErrorMessage name="description" component="div" />

            <label>Date</label>
            <Field name="date" type="date" />
            <ErrorMessage name="date" component="div" />

            <label>Trees Planted</label>
            <Field name="trees_planted" type="number" />
            <ErrorMessage name="trees_planted" component="div" />

            <label>Club</label>
            <Field name="club_id" as="select">
              <option value="">Select a club</option>
              {clubs.map((club) => (
                <option key={club.id} value={club.id}>
                  {club.name}
                </option>
              ))}
            </Field>
            <ErrorMessage name="club_id" component="div" />

            <button type="submit" disabled={isSubmitting}>
              Create Event
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default CreateEvent;
